mapper.select:
  select * from file
where filename like "%"#{key}"%" or summary like "%"#{key}"%"
limit #{index}, #{count}

select * from file
where type = #{type} and (filename like "%"#{key}"%" or summary like "%"#{key}"%")
limit #{index}, #{count}

mapper.insert:
insert into file (id, userid, filename, originname, summary,
  downloads, size, type,path, tag )
values (#{id,jdbcType=BIGINT}, #{userid,jdbcType=BIGINT}, #{filename,jdbcType=VARCHAR}, #{originname,jdbcType=VARCHAR}, #{summary,jdbcType=VARCHAR},
  #{downloads,jdbcType=INTEGER}, #{size,jdbcType=TINYINT}, #{type,jdbcType=TINYINT}, #{path,jdbcType=TINYINT}, #{tag}
  )

mapper.update:
update file
set userid = #{userid,jdbcType=BIGINT},
  filename = #{filename,jdbcType=VARCHAR},
  originname = #{filename,jdbcType=VARCHAR},
  downloads = #{downloads,jdbcType=INTEGER},
  size = #{size,jdbcType=TINYINT},
  type = #{type,jdbcType=TINYINT}
  tag = #{tag}
where id = #{id,jdbcType=BIGINT}
